// Important differences between import-results.js and other importers:
//
// For import-results, we don't expect any draggable labels, and so don't require
// any draggable targets.
//
// import-results assumes the csv file is in utf-8 (because the results file
// presumably was generated by us, not by some random spreadsheet program), and
// so doesn't bother to offer encoding choices.

function usingLabelTargets() {
  return false;
}

function onFileContentLoaded(file) {
  $('#import_button_div').removeClass('hidden');
  $('#csv_content td').removeClass('dim');
}

$(function() {
  $('#import_button').click(function() {
    uploadTableRowsWithParameters('result.import',
                                  ['class', 'round', 'heat', 'lane',
                                   'firstname', 'lastname', 'carnumber', 'carname',
                                   'finishtime', 'finishplace', 'scale-mph', 'completed']);
  });
});
